import React from 'react';

const ArticleList = ({articles}) => (
    <>
    {articles.map( (article, key)=> (
        <Link key={key} to={`/article/${article.name}`}>
            <h3>{article.title}</h3>
            <p>{article.content[0].substring(0,150)}...</p>
        </Link>

    ))}
    </>
);

export default ArticleList;
